import React, { useState } from 'react';

function App() {  
  // Nama
  const myName = 'Tyo';
  const [displayName, setDisplayName] = useState(myName);
  //  Button increment & decrement
  const [count, setCount] = useState(0);
  //  Merubah nama pada sebelumnya menjadi wazowski selama 3 detik
  function handleClick () {
    setDisplayName('Wazowski');
    setTimeout(() => {
      setDisplayName(myName);
    }, 3000);
  }
  //  Increment & Decrement btn
  function decrementCount () {
    setCount(prevCount => prevCount -1)
  }
  function incrementCount () {
    setCount(prevCount => prevCount +1)
  }
  //  Hover style

  //  React Style
  const btnInc = {
    padding: '10px 40px',
    fontSize: '20px',
    backgroundColor: 'white',
    border: '1px solid #000',
    cursor: 'pointer',
  }
  const countNum = {
    padding: '2px 20px',
    fontSize: '30px',
    border: 'none',
    marginRight: '5px',
    marginLeft: '5px',
    borderRadius: '15px',
  }
  const appwrap = {
    padding: '10px',
    justifyContent: 'center',
    textAlign: 'center',
  }
  const mynama = {
    padding: '3px',
    fontSize: '30px',
    fontWeight: 'bold',
    backgroundColor: '#fff',
    border: 'none',
    cursor: 'pointer',
    color: '#0377FF',
    textDecorationLine: 'underline',
  }
  return (
    <div className="App" style={appwrap}>
      <h1>Hello my name is <button style={mynama} onClick={handleClick}>{displayName}</button></h1>
      <h1>ðŸ‘‡ðŸ‘‡ðŸ‘‡</h1>
      <h3>(Click into the name above (<span className='blueone'>the blue one</span>) to change it for 3 second!)</h3>
      <h3>Click the button to increase and decrease the number below!</h3>
      <div className='btn-wrap'>
      <button style={btnInc} onClick={decrementCount}>-</button>
      <span style={countNum}>{count}</span>
      <button style={btnInc} onClick={incrementCount}>+</button>
      </div>
    </div>
  );
}

export default App;
